import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => {
  return <h1>{props.course}</h1>;
};

const Content = (props) => {
  return (
    <>
      <Part name={props.part1Name} ex={props.part1Ex}></Part>
      <Part name={props.part2Name} ex={props.part2Ex}></Part>
      <Part name={props.part3Name} ex={props.part3Ex}></Part>{" "}
    </>
  );
};
const Part = (props) => {
  return (
    <div>
      {props.name} {props.ex}{" "}
    </div>
  );
};
const Total = (props) => {
  return <>Total number of excercises: {props.e1 + props.e2 + props.e3}</>;
};
const App = () => {
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },
    {
      name: "Using props to pass data",
      exercises: 7,
    },
    {
      name: "State of a component",
      exercises: 14,
    },
  ];

  return (
    <div>
      <Header course={course} />
      <p>
        <Content part1Name={parts[0]} part1Ex={part1.exercises} />{" "}
      </p>{" "}
      <p>
        <Content part2Name={part2.name} part2Ex={part2.exercises} />
      </p>
      <p>
        <Content part3Name={part3.name} part3Ex={part3.exercises} />
      </p>{" "}
      <p>
        <Total
          e1={part1.exercises}
          e2={part2.exercises}
          e3={part3.exercises}
        ></Total>
      </p>
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
