import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => {
  return <h1>{props.course}</h1>;
};

const Content = (props) => {
  return (
    <>
      /* <Part name={props[0].name} ex={props[0].exercises}></Part>
      <Part name={props[1].name} ex={props[1].exercises}></Part>
      <Part name={props[2].name} ex={props[2].exercises}></Part> */
    </>
  );
};
const Part = (props) => {
  return (
    <div>
      {props.name} {props.ex}{" "}
    </div>
  );
};
const Total = (props) => {
  return (
    <>
      Total number of excercises:{" "}
      {props[0].exercises + props[1].exercises + props[2].exercises}
    </>
  );
};
const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },
    {
      name: "Using props to pass data",
      exercises: 7,
    },
    {
      name: "State of a component",
      exercises: 14,
    },
  ];

  console.log(parts[0].name);

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
